                     {{/if}}
                     {{#if (lowercase (dashcase name))  '===' "metal-frames"}}
                          <p class="mt-0 nav-price">Start at <b>$169.99</b></p>
                     {{/if}}
                     <!--TOPPERS -->
                     {{#if (lowercase (dashcase name))  '===' "2-5-inch-gel-memory-foam-mattress-topper"}}
                         <p class="mt-0 nav-price">Start at <b>$159.99</b></p>
                    {{/if}}
                    {{#if (lowercase (dashcase name))  '===' "3-inch-down-alternative-mattress-topper"}}
                      <p class="mt-0 nav-price">Start at <b>$129.99</b></p>
                    {{/if}}
                    {{#if (lowercase (dashcase name))  '===' "carboncool-lt-omniphase-topper"}}
                        <p class="mt-0 nav-price">Start at <b>$379.99</b></p>
                    {{/if}}
                     <!--BEDDING -->
                    </a>
                {{/if}}
            </li>
        {{/each}}
        {{/inArray}}
    </ul>
</div>
{{else}}
<a class="navPages-action{{#if is_active}} activePage{{/if}}"
   href="{{url}}"
   aria-label="{{name}}"
>

<span>{{name}}</span>
</a>
{{/if}}

components/common/social-links:
{{#if social_media}}
    <ul class="socialLinks socialLinks--alt">
        {{#each social_media}}
            <li class="socialLinks-item">
                <a class="icon icon--{{display_name}}" href="{{url}}" target="_blank" rel="noopener">
                    <svg><use xlink:href="#icon-{{display_name}}" /></svg>
                </a>
            </li>
        {{/each}}
    </ul>
{{/if}}
